import React, { Component } from 'react'
import { Pie } from 'react-chartjs-2'

class Piechart extends Component {
    // * Props can be deconstructed from meetData: {meetData (Meet Names), meetNumber (Array of number of occ. of each meet)}
    render() {

        // * If no data has been passed down from the form or invalid display empty form
        if (this.props.meetData == null) {
            data = {
                datasets: [
                    {
                        label: ''
                    }
                ]
            }
        } else {

            let data;
            const dataLength = this.props.meetData.length;
            const colorRangeInfo = {
                colorStart: 0,
                colorEnd: 1,
                useEndAsStart: false,
            };
            let colors = interpolateColors(dataLength, colorScale, colorRangeInfo);
            console.log(colors)
            data = {
                labels: this.props.meetData.meetName,
                datasets: [{
                    data: this.props.meetData.meetNumber,
                    backgroundColor: getRandomColor(this.props.meetData.meetName)
                }],

            }
        }


        /* Must use an interpolated color scale, which has a range of [0, 1] */
        function interpolateColors(dataLength, colorScale, colorRangeInfo) {
            let { colorStart, colorEnd } = colorRangeInfo;
            let colorRange = colorEnd - colorStart;
            let intervalSize = colorRange / dataLength;
            let i, colorPoint;
            let colorArray = [];

            for (i = 0; i < dataLength; i++) {
                colorPoint = calculatePoint(i, intervalSize, colorRangeInfo);
                colorArray.push(colorScale(colorPoint));
            }
            return colorArray;
        }

        return (
            <div>
                <h2>Pie Example</h2>
                <Pie data={data} height={100} />

            </div>
        )
    }
}
export default Piechart;
