import * as React from 'react';

import FormCheck from 'react-bootstrap/FormCheck';
import FormFile from 'react-bootstrap/FormFile';
import FormControl from 'react-bootstrap/FormControl';
import FormGroup from 'react-bootstrap/FormGroup';
import FormLabel from 'react-bootstrap/FormLabel';
import FormText from 'react-bootstrap/FormText';

import { BsPrefixComponent, BsPrefixRefForwardingComponent } from 'react-bootstrap/helpers';

export class FormRow<
  As extends React.ElementType = 'div'
  > extends BsPrefixComponent<As> { }

export interface FormProps {
  inline?: boolean;
  validated?: boolean;
}

declare interface Form
  extends BsPrefixRefForwardingComponent<'form', FormProps> {
  Row: typeof FormRow;
  Group: typeof FormGroup;
  Control: typeof FormControl;
  Check: typeof FormCheck;
  File: typeof FormFile;
  Label: typeof FormLabel;
  Text: typeof FormText;
}

declare const Form: Form;
export default Form;
